import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import './GuessingGame.css';
import profile from '../Assets/profile.png';
import Modal from 'react-modal';

const generateUniqueNumber = () => {
    const digits = '0123456789'.split('');
    let number = '';

    while (number.length < 4) {
        const index = Math.floor(Math.random() * digits.length);
        number += digits[index];
        digits.splice(index, 1);
    }
    console.log(number);
    return number;
};

Modal.setAppElement('#root');

const GuessingGame = () => {
    const [name, setName] = useState('');
    const [number, setNumber] = useState('');
    const [guess, setGuess] = useState('');
    const [feedback, setFeedback] = useState('');
    const [guesses, setGuesses] = useState(0);
    const [bestScore, setBestScore] = useState(null);
    const [startTime, setStartTime] = useState(null);
    const [timeTaken, setTimeTaken] = useState(null);
    const [timer, setTimer] = useState(0);
    const [isModalOpen, setIsModalOpen] = useState(false);
    const intervalRef = useRef(null);

    useEffect(() => {
        const fetchBestScore = async () => {
            const response = await axios.get('http://localhost:5000/api/scores/best');
            setBestScore(response.data);
        };

        fetchBestScore();
    }, []);

    useEffect(() => {
        if (startTime) {
            intervalRef.current = setInterval(() => {
                setTimer(Math.floor((Date.now() - startTime) / 1000));
            }, 1000);
        } else {
            clearInterval(intervalRef.current);
        }

        return () => clearInterval(intervalRef.current);
    }, [startTime]);

    const startNewGame = () => {
        const newNumber = generateUniqueNumber();
        setNumber(newNumber);
        setGuess('');
        setFeedback('');
        setGuesses(0);
        setStartTime(Date.now());
        setTimeTaken(null);
        setTimer(0);
    };

    const restartGame = () => {
        startNewGame();
    };

    const calculateScore = (time, guesses) => {
        const weightTime = 2;
        const weightGuesses = 1;
        return (weightTime * time + weightGuesses * guesses) / (weightTime + weightGuesses);
    };

    const handleGuess = async () => {
        console.log('User Guess:', guess);
        let newFeedback = '';
        let computerNumber = number.split('');
        let userGuess = guess.split('');
        let correct = 0;
        let incorrect = 0;

        for (let i = 0; i < userGuess.length; i++) {
            if (userGuess[i] === computerNumber[i]) {
                correct++;
                computerNumber[i] = null;
            } else if (computerNumber.includes(userGuess[i])) {
                incorrect++;
            }
        }

        newFeedback = '+'.repeat(correct) + '-'.repeat(incorrect);
        setFeedback(newFeedback);
        setGuesses(guesses + 1);

        if (correct === 4) {
            const endTime = Date.now();
            const totalTimeTaken = (endTime - startTime) / 1000;
            setTimeTaken(totalTimeTaken);
            const score = calculateScore(totalTimeTaken, guesses + 1);

            const response = await axios.post('http://localhost:5000/api/scores', {
                name,
                score,
                guesses: guesses + 1,
                timeTaken: totalTimeTaken
            });

            setBestScore(response.data);
            clearInterval(intervalRef.current);
            setIsModalOpen(true);
        }
    };

    return (
        <div className="container">
            <h1>Guess the Number Game</h1>
            {!number ? (
                <>
                    <input
                        type="text"
                        placeholder="Enter your name"
                        value={name}
                        onChange={(e) => setName(e.target.value)}
                    />
                    <button onClick={startNewGame}>Start New Game</button>
                </>
            ) : (
                <>
                    <h2>Guess the 4-digit number</h2>
                    <div className="timer"><h2>Time: {timer}s</h2></div>
                    <div className="userName">
                        <span><img src={profile} alt="User Profile" /></span>
                        <p>{name}</p>
                    </div>
                    <input
                        type="text"
                        maxLength="4"
                        value={guess}
                        onChange={(e) => setGuess(e.target.value)}
                    />
                    <button onClick={handleGuess}>Submit Guess</button>
                    <p className="feedback">Feedback: {feedback}</p>
                    <p>Guesses: {guesses}</p>
                    <button onClick={restartGame}>Restart Game</button>
                </>
            )}
            <p className="srinath">Copyright @srinath 2024 - All Rights Reserved.</p>

            <Modal isOpen={isModalOpen} onRequestClose={() => setIsModalOpen(false)} className="modal">
                <h2>Best Score</h2>
                {bestScore && (
                    <div className="best-score">
                        <p>Name: {bestScore.name}</p>
                        <p>Score: {bestScore.score}</p>
                        <p>Guesses: {bestScore.guesses}</p>
                        <p>Time Taken: {bestScore.timeTaken}s</p>
                    </div>
                )}
                <button onClick={() => setIsModalOpen(false)}>Close</button>
            </Modal>
        </div>
    );
};

export default GuessingGame;
